class MapEncoder{static decodeMap(t,e,l){const n=[];let o=0,s=0;for(let c=0;c<t.length;c+=2){const r=t[c],u=t[c+1];n[o]||(n[o]=[]);for(let t=0;t<u;t++)n[o][s]=r,s++,s>=e&&(s=0,o++,o<l&&!n[o]&&(n[o]=[]))}return n}static encodeMap(t){const e=[];let l=null,n=0;const o=[];for(let e=0;e<t.length;e++)for(let l=0;l<t[e].length;l++)o.push(t[e][l]);for(let t=0;t<o.length;t++){const s=o[t];s===l?n++:(null!==l&&e.push(l,n),l=s,n=1)}return null!==l&&e.push(l,n),e}}module.exports=MapEncoder;